import React from 'react'

class Login extends React.Component{
    constructor(){
        super()
        this.state = {
            isLogged : false,
            loading : false,
            users : {}
        }
    }

    componentDidMount(){
        this.setState({loading : true})
        fetch("/api/users/27/")
        .then(response => response.json())
        .then(data => 
          this.setState({
              loading : false,
              users : data
          }))
    }

    render(){
        const loadingText = this.state.loading ? "loading..." : this.state.users.name
        return(
            <div>
                {loadingText}
            </div>
        )
    }

}

export default Login
