import React from 'react'

class Login extends React.Component{
    constructor(){
        super()
        this.state = {
            isLogged : false,
            loading : false
        }
    }

    componentDidMount(){
        this.setState({loading : true})
        fetch("/api/users/27/")
        .then(response => response.json())
        .then(data => 
          this.setState({
              loading : false,
              sport : data
          }))
    }

    render(){
        const loadingText = this.state.loading ? "loading..." : this.state.sport.name
        return(
            <div>
                {loadingText}
            </div>
        )
    }

}

export default Login
