import React from 'react'

class Sport extends React.Component{
    constructor(){
        super()
        this.state = {
            loading : false,
            sport : {}

        }
    }

    componentDidMount(){
        this.setState({loading : true})
        fetch("http://localhost:8080/sport/3")
        .then(response => response.json())
        .then(data => 
          this.setState({
            sport : data,
            loading : false
          }
          ))
      }

    render(){
            const text = this.state.loading ? "veuillez patienter..." : 
            this.state.sport.name
    return(
      <div>
      {text}
      </div>
        )
    }

}

export default Sport
